mapper.select:
SELECT
,

FROM pd_purchase_order a



    a.del_flag =#{DEL_FLAG_NORMAL}

        AND a.id = #{id}


        AND a.order_no = #{orderNo}


        AND a.dept_name = #{deptName}


        AND a.order_status = #{orderStatus}


        AND a.submit_start = #{submitStart}

SELECT DISTINCT a.id as purchaseId, a.order_no as orderNo, a.order_date as orderDate, a.dept_name as deptName, a.order_status as orderStatus, c.realname as auditByName FROM pd_purchase_order a LEFT JOIN pd_purchase_detail b on a.order_no = b.order_no LEFT JOIN sys_user c ON c.id = a.audit_by LEFT JOIN pd_product d on b.product_id = d.id
a.del_flag = #{DEL_FLAG_NORMAL} AND a.order_status = '2'
AND a.id = #{id}
AND a.order_no LIKE concat('%',#{orderNo},'%')
AND ( d.name LIKE concat('%',#{productName},'%') or d.py LIKE concat('%',#{productName},'%') or d.wb LIKE concat('%',#{productName},'%') or d.bname LIKE concat('%',#{productName},'%') or d.bpy LIKE concat('%',#{productName},'%') or d.bwb LIKE concat('%',#{productName},'%') or d.zdy LIKE concat('%',#{productName},'%') )
AND d.supplier_id = #{supplierId}
AND d.number LIKE concat('%',#{number},'%')
AND a.order_date >= #{queryDateStart}
AND a.order_date <= #{queryDateEnd}
order by a.order_date desc
